mapper.insert:
INSERT INTO bid_increment_rule (
    config_id, min_amount, max_amount, increment_amount, sort_order, create_time, update_time, deleted
) VALUES (
    #{configId}, #{minAmount}, #{maxAmount}, #{incrementAmount}, #{sortOrder}, #{createTime}, #{updateTime}, #{deleted}
)

mapper.update:
UPDATE bid_increment_rule
SET deleted = 1, update_time = NOW()
WHERE config_id = #{configId}

UPDATE bid_increment_rule
SET deleted = 1, update_time = NOW()
WHERE id = #{id}
