mapper.insert:
INSERT INTO sys_config (
    config_key, config_value, config_type, description, 
    is_system, is_editable, create_time, update_time, deleted
) VALUES (
    #{configKey}, #{configValue}, #{configType}, #{description},
    #{isSystem}, #{isEditable}, #{createTime}, #{updateTime}, #{deleted}
)

mapper.update:
UPDATE sys_config
SET deleted = 1, update_time = NOW()
WHERE id = #{id}

UPDATE sys_config
SET deleted = 1, update_time = NOW()
WHERE config_key = #{configKey}
